INSERT INTO `classify` (`id`, `name`, `description`, `icon_path`, `cover_path`, `person_count`, `use_type`, `sort`, `create_time`, `update_time`) VALUES (1, '中国国家图书馆', '中国国家图书馆位于北京市中关村南大街33号，与白石桥高粱河、紫竹院公园相邻，筹建于1909年的世界规模最大技术最先进的国家图书馆之一，也是馆藏文献达3768.62万册，数字资源超1000TB的亚洲规模最大的图书馆，并曾入选第三批中国20世纪建筑遗产项目。', '/upload/2024-01-30/pro1_20240130091209_2222419.jpg', '/upload/2024-01-29/desc1_20240129174624_9376900.jpg', NULL, 1, 1, '2022-11-09 16:52:11', '2024-01-30 09:12:11');
INSERT INTO `classify` (`id`, `name`, `description`, `icon_path`, `cover_path`, `person_count`, `use_type`, `sort`, `create_time`, `update_time`) VALUES (2, '俄罗斯国立图书馆', '这个共计藏有4280万件藏书，隶属于俄罗斯文化部，始建于19世纪60年代的欧洲第一大图书馆，是坐落在克里姆林宫旁的帕什科夫楼，馆藏文献涉及247个鱼种，有着“俄罗斯的记忆”美誉，经历了超百年历史的世界规模较大的图书馆之一。', '/upload/2024-01-30/pro2_20240130091214_1554486.jpg', '/upload/2024-01-29/desc2_20240129174636_1334519.jpg', NULL, 1, 2, '2022-11-09 16:52:37', '2024-01-30 09:12:16');
INSERT INTO `classify` (`id`, `name`, `description`, `icon_path`, `cover_path`, `person_count`, `use_type`, `sort`, `create_time`, `update_time`) VALUES (3, '美国国会图书馆', '这座建成于1800年华盛顿国会山上的国会图书馆，是世界上最大的图书馆，也是全球最重要的图书馆之一。\n它藏有7500多万件作品、各类收藏近1亿2100多万项，其中三分之二的书籍都是以多媒体形式进行存放，除了图书外，还拥有特色收藏、电影胶片、世界最大的地图等藏品类别。', '/upload/2024-01-30/pro3_20240130091220_2531211.jpg', '/upload/2024-01-29/desc3_20240129174645_2786327.jpg', NULL, NULL, 3, '2022-11-29 17:34:24', '2024-01-30 09:12:21');
INSERT INTO `classify` (`id`, `name`, `description`, `icon_path`, `cover_path`, `person_count`, `use_type`, `sort`, `create_time`, `update_time`) VALUES (6, '法国国家图书馆', '法国国家图书馆这处为查理五世收藏的历代王室藏书而建立的世界大星图书馆之一，在弗朗索瓦一世时期又经\n过重建，经过皇家图书馆发展而来的国王图书馆，这个拥有五百多年历史的图书馆从1786年馆藏就达到了15万册。', '/upload/2024-01-30/pro4_20240130091225_1915703.jpg', '/upload/2024-01-29/desc4_20240129174653_9222828.jpg', NULL, NULL, 4, '2023-01-05 17:24:32', '2024-01-30 09:12:26');
INSERT INTO `classify` (`id`, `name`, `description`, `icon_path`, `cover_path`, `person_count`, `use_type`, `sort`, `create_time`, `update_time`) VALUES (7, '大英图书馆', '大英图书馆是世界上最大的学术图书馆之一。根据1972年颁布的《英国图书馆法》于1973年7月1日建立。它是由国立中央图书馆、前大英博物馆图书馆、国立外界科技图书馆等单位共同组成，藏有1.5亿藏品，并拥有古老至公元前二千年古籍手抄本的全球信\n息储藏库之一。', '/upload/2024-01-30/pro5_20240130091228_1938490.jpg', '/upload/2024-01-29/desc5_20240129174656_5645221.jpg', NULL, NULL, 5, '2023-01-05 17:24:53', '2025-03-18 14:41:10');
