mapper.select:
SELECT
id, parent_id, level, node_path, name_path, id_path, title, permission_str, name, path, component, icon, create_time, update_time, sort
FROM
permission
WHERE
JSON_OVERLAPS(node_path,
JSON_ARRAY(

        #{id}

    )
)

WITH RECURSIVE ancestors AS ( -- 初始：从这批 ID 出发 SELECT id, parent_id, level, node_path, name_path, id_path, title, permission_str, name, path, component, icon, create_time, update_time, sort FROM permission WHERE id IN
#{id}
UNION ALL -- 递归：往上找 parent SELECT m.id, m.parent_id, m.level, m.node_path, m.name_path, m.id_path, m.title, m.permission_str, m.name, m.path, m.component, m.icon, m.create_time, m.update_time, m.sort FROM permission m JOIN ancestors a ON m.id = a.parent_id ) SELECT DISTINCT * FROM ancestors;
