mapper.select:
SELECT id, parent_id, node_path, name_path, id_path, type, title, permission_str, ui_meta, sort, create_time, update_time FROM permission WHERE JSON_OVERLAPS(node_path, JSON_ARRAY(
#{id}
) )

WITH RECURSIVE ancestors AS ( -- 初始：从这批 ID 出发 SELECT id, parent_id, node_path, name_path, id_path, type, title, permission_str, ui_meta, sort, create_time, update_time FROM permission WHERE id IN
#{id}
UNION ALL -- 递归：往上找 parent SELECT m.id, m.parent_id, m.node_path, m.name_path, m.id_path, m.type, m.title, m.permission_str, m.ui_meta, m.sort, m.create_time, m.update_time FROM permission m JOIN ancestors a ON m.id = a.parent_id ) SELECT DISTINCT * FROM ancestors;

SELECT permission.id, permission.parent_id, permission.node_path, permission.name_path, permission.id_path, permission.type, permission.title, permission.permission_str, permission.ui_meta, permission.sort, permission.create_time, permission.update_time
FROM admin_info INNER JOIN admin_role_relate ON admin_info.id = admin_role_relate.admin_id INNER JOIN role ON admin_role_relate.role_id = role.id INNER JOIN role_permission_relate ON role.id = role_permission_relate.role_id INNER JOIN permission ON role_permission_relate.permission_id = permission.id WHERE admin_info.id = #{userId}
order by permission.sort, permission.id
