mapper.select:
SELECT sr.id, sr.start_time, sr.user_id, sr.actual_duration, sr.classify_id, sr.note_status, sr.status, c.name as classifyName from study_record sr INNER JOIN classify c on c.id = sr.classify_id
sr.user_id = #{userId}
and sr.classify_id = #{classifyId}
and sr.status = #{status}

SELECT
count(1)
from study_record sr
INNER JOIN classify c on c.id = sr.classify_id


        sr.user_id = #{userId}


        and sr.classify_id = #{classifyId}


        and sr.status = #{status}

select sr.id, sr.user_id, u.name, u.phone, sr.classify_id, cl.`name` as classifyName, sr.catalog_id, ca.`name` as catalogName, (select cat.name from catalog cat where cat.id = ca.parent_id) as parentCatalogName, sr.tag, sr.seat, sr.timing_mode, sr.setting_duration, sr.actual_duration, sr.`status`, sr.start_time, sr.note_status, sr.note_content, sr.note_path from study_record sr INNER JOIN user u on sr.user_id = u.id INNER JOIN classify cl on sr.classify_id = cl.id INNER JOIN catalog ca on sr.catalog_id = ca.id
and sr.classify_id = #{classifyId}
and sr.status = #{status}
and u.phone like concat ('%',#{phone},'%')

select COUNT(1) from study_record sr INNER JOIN user u on sr.user_id = u.id INNER JOIN classify cl on sr.classify_id = cl.id INNER JOIN catalog ca on sr.catalog_id = ca.id
and sr.classify_id = #{classifyId}
and sr.status = #{status}
and u.phone like concat ('%',#{phone},'%')
